import './css/style.css';
import {Component} from "react";

class Profile extends Component{

    state = {
        name: 'Clément GEYER',
        studies: 'DUT Informatique 2ème année',
        domain: 'Développement Web'
    }

    render(){
        return(
            <div className={"profile"}>
                <style>
                    @import url('https://fonts.googleapis.com/css2?family=Roboto:wght@100&display=swap');
                </style>
                <p className={"profile_name"}>{this.state.name}</p>
                <div className={"profile_infos"}>
                    <p onMouseEnter={this.grow.bind(this, true)} onMouseLeave={this.grow.bind(this, false)}>{this.state.studies}</p>
                    <p onMouseEnter={this.grow.bind(this, true)} onMouseLeave={this.grow.bind(this, false)}>{this.state.domain}</p>
                </div>
                <hr/>
            </div>

        )
    }

    grow = (enter, e) => {
        enter ? e.target.style.border = "2px solid #7bff7e" : e.target.style.border = "2px solid grey";
        enter ? e.target.style.opacity = "100%" : e.target.style.opacity = "50%";
    }
}


export default Profile;
